<script lang="ts">
import { Vue, Component, Prop, Watch } from 'vue-property-decorator'

@Component
export default class Termometro extends Vue {
 @Prop({ default: 0 }) private percent!: number

  $refs!: {
     master: SVGLinearGradientElement;
     svg: SVGElement;
  }

  @Watch('percent')
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  newValue (newValue: number, oldValue: number) {
    const newValueCalc = newValue >= 100 ? 100 : newValue
    const calc1 = ((0.9998 * (100 - newValueCalc)) / 100)
    const calc2 = calc1 + 0.0001
    this.$refs.master.setAttribute('y1', String(calc1))
    this.$refs.master.setAttribute('y2', String(calc2))
    if (newValueCalc === 100) {
      this.svgVerify()
    } else {
      this.remover()
    }
  }

  svgVerify () {
    this.$refs.svg.classList.add('animate__animated', 'animate__pulse', 'animate__infinite')
  }

  remover () {
    this.$refs.svg.classList.remove('animate__animated', 'animate__jello', 'animate__infinite')
  }
}
</script>

